    private void jbtnConvertActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnConvertActionPerformed
        
        if ((jtxtEnter1.getText().equals("")) || (jtxtEnter2.getText().equals("")))
        {
            JOptionPane.showMessageDialog(null, "You must enter value to compute", "DST System", JOptionPane.INFORMATION_MESSAGE);
        }
        
        else if (jSItem.getSelectedItem().equals("Distance"))
        {
            double dst1 = Double.parseDouble(jtxtEnter1.getText());
            double dst2 = Double.parseDouble(jtxtEnter2.getText());
            double dst3;
            
            jlblDisplay.setText("Distance = Speed X Time");                //Dst = Spd x Time
            dst3 = dst1 * dst2;
            String value = String.format("%.2f", dst3);
            jlblResult.setText(value);
        }
        
        else if (jSItem.getSelectedItem().equals("Speed"))
        {
            double dst1 = Double.parseDouble(jtxtEnter1.getText());
            double dst2 = Double.parseDouble(jtxtEnter2.getText());
            double dst3;
            
            jlblDisplay.setText("Speed = Distance / Time");                   //Spd = Dst / Time
            dst3 = dst1 / dst2;
            String value = String.format("%.2f", dst3);
            jlblResult.setText(value);
        }
        else if (jSItem.getSelectedItem().equals("Time"))
        {
            double dst1 = Double.parseDouble(jtxtEnter1.getText());
            double dst2 = Double.parseDouble(jtxtEnter2.getText());
            double dst3;
            
            jlblDisplay.setText("Time = Distance / Speed ");                    //Time = Dst / Spd  
            dst3 = (dst1 * dst2) / dst1;
            String value = String.format("%.2f", dst3);
            jlblResult.setText(value);
        }
    }//GEN-LAST:event_jbtnConvertActionPerformed

    private void jbtnResetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnResetActionPerformed
        jlblResult.setText(null);
        jlblDisplay.setText(null);                    
        jtxtEnter1.setText(null);
        jtxtEnter2.setText(null);
        jSItem.getSelectedItem().equals("Select an Option"); 
    }//GEN-LAST:event_jbtnResetActionPerformed

